                                            <Bot size={24} className="text-white" />
                                        </div>
                                        <div className="flex-1">
                                            <h3 className="text-lg font-semibold text-white">OpenAI</h3>
                                            <p className="text-sm text-gray-400">GPT-4 & other models</p>
